class h{constructor(t,i,s,b,d,n){this.color=t,this.posX=i,this.posY=s,this.speedX=b,this.speedY=d,this.size=n,this.img=new Image,this.img.src="/static/gamesImage/RabbitDance/rabbits/rabbit1.png"}draw(t){t.drawImage(this.img,this.posX,this.posY,this.size,this.size)}update(t){this.posX+=this.speedX,this.posY+=this.speedY}}class o{constructor(){this.rabbitsLeft=[],this.rabbitsRight=[]}rand(t,i){return Math.floor(Math.random()*(i-t+1))+t}createRabbits(t){for(var i=0;i<this.rand(2,6);i++)this.rabbitsLeft.push(new h("white",this.rand(0,100),this.rand(0,100),this.rand(.5,3),this.rand(.5,3),this.rand(40,60)));for(var i=0;i<this.rand(2,6);i++)this.rabbitsRight.push(new h("white",this.rand(t.width-100,t.width),this.rand(t.height-100,t.height),this.rand(-.5,-3),this.rand(-.5,-3),this.rand(40,60)))}drawRabbits(t,i){t.clearRect(0,0,i.width,i.height),this.rabbitsLeft.forEach(s=>{s.draw(t)}),this.rabbitsRight.forEach(s=>{s.draw(t)})}updateRabbits(t){this.rabbitsLeft.forEach(i=>{i.update(t)}),this.rabbitsRight.forEach(i=>{i.update(t)})}play(t,i){this.drawRabbits(t,i),this.updateRabbits(t),requestAnimationFrame(()=>this.play(t,i))}}var a=document.getElementById("canvasGames"),p=a.getContext("2d");let e=new o;e.createRabbits(a);e.play(p,a);
