class a{constructor(t,i,s,d,b,o){this.color=t,this.posX=i,this.posY=s,this.speedX=d,this.speedY=b,this.size=o,this.img=new Image,this.img.src="/storage/static/hiiro.png"}draw(t){t.drawImage(this.img,this.posX,this.posY,this.size,this.size)}update(t){this.posX+=this.speedX,this.posY+=this.speedY}}class n{constructor(){this.rabbitsLeft=[],this.rabbitsRight=[]}rand(t,i){return Math.floor(Math.random()*(i-t+1))+t}createRabbits(t){for(var i=0;i<this.rand(2,6);i++)this.rabbitsLeft.push(new a("white",this.rand(0,100),this.rand(0,100),this.rand(.5,3),this.rand(.5,3),this.rand(40,60)));for(var i=0;i<this.rand(2,6);i++)this.rabbitsRight.push(new a("white",this.rand(t.width-100,t.width),this.rand(t.height-100,t.height),this.rand(-.5,-3),this.rand(-.5,-3),this.rand(40,60)))}drawRabbits(t,i){t.clearRect(0,0,i.width,i.height),this.rabbitsLeft.forEach(s=>{s.draw(t)}),this.rabbitsRight.forEach(s=>{s.draw(t)})}updateRabbits(t){this.rabbitsLeft.forEach(i=>{i.update(t)}),this.rabbitsRight.forEach(i=>{i.update(t)})}play(t,i){this.drawRabbits(t,i),this.updateRabbits(t),requestAnimationFrame(()=>this.play(t,i))}}var h=document.getElementById("canvasGames"),p=h.getContext("2d");let r=new n;r.createRabbits(h);r.play(p,h);
